INSERT INTO keajaiban_dunia (nm_wonderful, geografis, tahun_dibangun, negara, deskripsi) VALUES
('Great Wall of China', 'China', 7, 'China', 'The Great Wall of China is a series of fortifications made of stone, brick, tamped earth, wood, and other materials, generally built along an east-to-west line across the historical northern borders of China to protect the Chinese states and empires against the raids and invasions of the various nomadic groups of the Eurasian Steppe.'),
('Petra', 'Jordan', 312, 'Jordan', 'Petra, originally known to its inhabitants as Raqmu, is a historical and archaeological city in southern Jordan.'),
('Machu Picchu', 'Peru', 1450, 'Peru', 'Machu Picchu is a 15th-century Inca citadel located in the Eastern Cordillera of southern Peru on a mountain ridge 2,430 meters above sea level.'),
('Colosseum', 'Italy', 80, 'Italy', 'The Colosseum is an oval amphitheatre in the centre of the city of Rome, Italy.'),
('Taj Mahal', 'India', 1632, 'India', 'The Taj Mahal is an ivory-white marble mausoleum on the right bank of the river Yamuna in the Indian city of Agra.'),
('Chichen Itza', 'Mexico', 800, 'Mexico', 'Chichen Itza was a large pre-Columbian city built by the Maya people of the Terminal Classic period. The archaeological site is located in Tinúm Municipality, Yucatán State, Mexico.'),
('Christ the Redeemer', 'Brazil', 1931, 'Brazil', 'Christ the Redeemer is an Art Deco statue of Jesus Christ in Rio de Janeiro, Brazil, created by French sculptor Paul Landowski and built by Brazilian engineer Heitor da Silva Costa, in collaboration with French engineer Albert Caquot.'),
('Roman Baths', 'United Kingdom', 70, 'United Kingdom', 'The Roman Baths complex is a site of historical interest in the English city of Bath. It is a well-preserved Roman site once used for public bathing.'),
('Kremlin and Red Square', 'Russia', 1495, 'Russia', "The Moscow Kremlin, or simply the Kremlin, is a fortified complex in the center of Moscow, overlooking the Moskva River to the south, Saint Basil\'s Cathedral and Red Square to the east, and the Alexander Garden to the west."),
('Stonehenge', 'United Kingdom', 2000, 'United Kingdom', 'Stonehenge is a prehistoric monument in Wiltshire, England, two miles west of Amesbury. It consists of a ring of standing stones, each around 13 feet high, seven feet wide, and weighing around 25 tons.');
